// Task: 创建语音控制面板UI组件

import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import '../../bloc/game_bloc.dart';
import '../../bloc/game_event.dart';
import '../../bloc/game_state.dart';

/// 语音控制面板组件
/// 
/// 功能：
/// - 语音控制开关按钮
/// - 麦克风状态指示器
/// - 识别结果实时反馈
/// - 语音指令帮助提示
class VoiceControlPanel extends StatelessWidget {
  const VoiceControlPanel({super.key});

  @override
  Widget build(BuildContext context) {
    return BlocBuilder<GameBloc, GameState>(
      builder: (context, state) {
        if (state is! GamePlaying) {
          return const SizedBox.shrink();
        }

        return Card(
          margin: const EdgeInsets.all(8.0),
          child: Padding(
            padding: const EdgeInsets.all(12.0),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              mainAxisSize: MainAxisSize.min,
              children: [
                // 标题和开关
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    const Row(
                      children: [
                        Icon(Icons.mic, size: 20),
                        SizedBox(width: 8),
                        Text(
                          '语音控制',
                          style: TextStyle(
                            fontSize: 16,
                            fontWeight: FontWeight.bold,
                          ),
                        ),
                      ],
                    ),
                    Switch(
                      value: state.isVoiceControlEnabled,
                      onChanged: (value) {
                        context.read<GameBloc>().add(
                          VoiceControlToggledEvent(value),
                        );
                      },
                    ),
                  ],
                ),

                if (state.isVoiceControlEnabled) ...[
                  const Divider(),

                  // 麦克风状态指示器
                  _buildMicrophoneStatus(state),

                  const SizedBox(height: 8),

                  // 识别结果显示
                  if (state.lastVoiceCommand != null)
                    _buildRecognitionResult(state),

                  const SizedBox(height: 8),

                  // 语音指令帮助
                  _buildVoiceHelp(context),
                ],
              ],
            ),
          ),
        );
      },
    );
  }

  /// 构建麦克风状态指示器
  Widget _buildMicrophoneStatus(GamePlaying state) {
    Color statusColor;
    IconData statusIcon;
    String statusText;

    if (state.isAIThinking) {
      statusColor = Colors.orange;
      statusIcon = Icons.hourglass_empty;
      statusText = '等待对方...';
    } else if (state.lastVoiceCommand != null) {
      statusColor = Colors.green;
      statusIcon = Icons.check_circle;
      statusText = '识别成功';
    } else {
      statusColor = Colors.blue;
      statusIcon = Icons.mic;
      statusText = '正在监听...';
    }

    return Container(
      padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 8),
      decoration: BoxDecoration(
        color: statusColor.withOpacity(0.1),
        borderRadius: BorderRadius.circular(8),
        border: Border.all(color: statusColor.withOpacity(0.3)),
      ),
      child: Row(
        children: [
          Icon(statusIcon, color: statusColor, size: 20),
          const SizedBox(width: 8),
          Text(
            statusText,
            style: TextStyle(
              color: statusColor,
              fontWeight: FontWeight.w500,
            ),
          ),
        ],
      ),
    );
  }

  /// 构建识别结果显示
  Widget _buildRecognitionResult(GamePlaying state) {
    final confidence = (state.voiceCommandConfidence * 100).toStringAsFixed(0);

    return Container(
      padding: const EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: Colors.green.withOpacity(0.1),
        borderRadius: BorderRadius.circular(8),
        border: Border.all(color: Colors.green.withOpacity(0.3)),
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          const Text(
            '识别结果:',
            style: TextStyle(
              fontSize: 12,
              color: Colors.grey,
            ),
          ),
          const SizedBox(height: 4),
          Row(
            children: [
              Expanded(
                child: Text(
                  state.lastVoiceCommand ?? '',
                  style: const TextStyle(
                    fontSize: 16,
                    fontWeight: FontWeight.bold,
                    color: Colors.green,
                  ),
                ),
              ),
              Text(
                '置信度: $confidence%',
                style: TextStyle(
                  fontSize: 12,
                  color: Colors.grey[600],
                ),
              ),
            ],
          ),
        ],
      ),
    );
  }

  /// 构建语音指令帮助
  Widget _buildVoiceHelp(BuildContext context) {
    return ExpansionTile(
      title: const Row(
        children: [
          Icon(Icons.help_outline, size: 18),
          SizedBox(width: 8),
          Text(
            '支持的语音指令',
            style: TextStyle(fontSize: 14),
          ),
        ],
      ),
      tilePadding: EdgeInsets.zero,
      childrenPadding: const EdgeInsets.only(left: 16, bottom: 8),
      children: [
        _buildHelpItem('传统坐标', '横1竖2、横一竖二'),
        _buildHelpItem('国际坐标', 'A1、B2、C3、D4'),
        _buildHelpItem('方向坐标', '左上、右下、中间'),
        _buildHelpItem('自然语言', '移动到横2竖3'),
      ],
    );
  }

  /// 构建帮助项
  Widget _buildHelpItem(String title, String examples) {
    return Padding(
      padding: const EdgeInsets.only(bottom: 8),
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(
            '$title:',
            style: const TextStyle(
              fontSize: 13,
              fontWeight: FontWeight.w500,
            ),
          ),
          const SizedBox(width: 8),
          Expanded(
            child: Text(
              examples,
              style: TextStyle(
                fontSize: 13,
                color: Colors.grey[600],
              ),
            ),
          ),
        ],
      ),
    );
  }
}

/// 浮动语音控制按钮（简化版，用于小屏幕）
class VoiceControlFloatingButton extends StatelessWidget {
  const VoiceControlFloatingButton({super.key});

  @override
  Widget build(BuildContext context) {
    return BlocBuilder<GameBloc, GameState>(
      builder: (context, state) {
        if (state is! GamePlaying) {
          return const SizedBox.shrink();
        }

        return FloatingActionButton(
          heroTag: 'voice_control',
          onPressed: () {
            context.read<GameBloc>().add(
              VoiceControlToggledEvent(!state.isVoiceControlEnabled),
            );
          },
          backgroundColor: state.isVoiceControlEnabled
              ? Colors.blue
              : Colors.grey,
          child: Icon(
            state.isVoiceControlEnabled ? Icons.mic : Icons.mic_off,
          ),
        );
      },
    );
  }
}
